def expected_array_length(m):
    big = 100010
    def gen_mu():
        mu = [1]*big
        mu[0] = 0
        P = [True]*big
        P[0] = P[1] = False
        for i in range(2,big):
            if P[i]:
                j = i
                while j<big:
                    P[j] = False
                    mu[j] *= -1
                    j += i
                j = i*i
                while j<big:
                    mu[j] = 0
                    j += i*i
        return mu

    mu = gen_mu()

    MOD = 10**9+7
    def mod_inv(x):
        return pow(x, MOD-2, MOD)

    s = 1
    for i in range(2,big):
        # p is probabilty that i | a random number [1,m]
        p = (m//i)*mod_inv(m)
        s += (-mu[i])*(p)*mod_inv(1-p)
    return s%MOD

def min_paint_for_beauty(n, m, k_res, arr, cost):
    INF = 10 ** 18
    MX_SZ = 112
    dp = [[[INF for k in range (MX_SZ)] for j in range (MX_SZ)] for i in range (MX_SZ)]
    best = [[[(INF, INF) for k in range (MX_SZ)] for j in range (MX_SZ)] for i in range (MX_SZ)]

    dp[0][0][MX_SZ - 1] = 0
    best[0][0][0] = (0, MX_SZ - 1)

    for i in range (1, n + 1):
        clr = arr[i - 1]
        if clr == 0:
            for j in range (1, k_res + 1):
                for k in range (1, m + 1):
                    dp[i][j][k] = dp[i - 1][j][k] + cost[i - 1][k - 1]
                    if k == best[i - 1][j - 1][0][1]:
                        dp[i][j][k] = min(dp[i][j][k], best[i - 1][j - 1][1][0] + cost[i - 1][k - 1])
                    else:
                        dp[i][j][k] = min(dp[i][j][k], best[i - 1][j - 1][0][0] + cost[i - 1][k - 1])
                    if dp[i][j][k] < best[i][j][0][0]:
                        best[i][j][1] = best[i][j][0]
                        best[i][j][0] = (dp[i][j][k], k)
                    elif dp[i][j][k] < best[i][j][1][0]:
                        best[i][j][1] = (dp[i][j][k], k)
        else:
            for j in range (1, n + 1):
                dp[i][j][clr] = dp[i - 1][j][clr]
                if clr == best[i - 1][j - 1][0][1]:
                    dp[i][j][clr] = min(dp[i][j][clr], best[i - 1][j - 1][1][0])
                else:
                    dp[i][j][clr] = min(dp[i][j][clr], best[i - 1][j - 1][0][0])
                best[i][j][0] = (dp[i][j][clr], clr)

    ans = INF
    for k in range (1, m + 1):
        if dp[n][k_res][k] < ans:
            ans = dp[n][k_res][k]
    if ans == INF:
        ans = -1
    return ans

def recover_chat_senders(chat_data):
    def pr(name, lvl, dp, u, tot, result):
        if lvl == 0:
            result.append(name + ':' + tot[lvl])
            return
        pr(u[lvl][name], lvl - 1, dp, u, tot, result)
        result.append(name + ':' + tot[lvl])

    users = chat_data[0]
    messages = chat_data[1]
    n = len(users)
    m = len(messages)

    dp = []
    u = []
    tot = []

    for i in range(m):
        dp.append(set())
        u.append({})
        line = messages[i].split(':')
        sender = line[0]
        tot.append(line[1])
        line[1] = line[1].replace('?', ' ')
        line[1] = line[1].replace('.', ' ')
        line[1] = line[1].replace(',', ' ')
        line[1] = line[1].replace('!', ' ')
        mess = line[1].split()

        if sender == '?':
            if i != 0:
                for name in users:
                    for x in dp[i-1]:
                        if x != name and name not in mess:
                            dp[i].add(name)
                            u[i][name] = x
            else:
                for name in users:
                    if name not in mess:
                        dp[i].add(name)
        else:
            if i != 0:
                for x in dp[i-1]:
                    if x != sender:
                        dp[i].add(sender)
                        u[i][sender] = x
            else:
                dp[i].add(sender)

    if dp[m-1]:
        result = []
        pr(list(dp[m-1])[0], m-1, dp, u, tot, result)
        return result
    else:
        return ['Impossible']

