import numpy as np

def products(n, min_divisor, max_divisor):      
    if n == 1:
        yield []
    for divisor in range(min_divisor, max_divisor+1):
        if n % divisor == 0:
            for product in products(n // divisor, divisor, max_divisor):
                yield product + [divisor]

