def min_computer_strength(n, strengths, edges):
    st = strengths[:]
    d = {}
    for x in range(n):
        d[x] = []

    st = [(st[i], i) for i in range(len(st))]
    st = sorted(st)

    for edge in edges:
        u, v = edge[0] - 1, edge[1] - 1
        d[u].append(v)
        d[v].append(u)

    hardest = []
    almost = []

    single_hardest = st[-1][0]

    for x in st[::-1]:
        if x[0] == single_hardest:
            hardest.append(x[1])
        elif x[0] == single_hardest-1:
            almost.append(x[1])
        else:
            break

    def inter(a, b):
        c = []
        for x in a:
            if x in b:
                c.append(x)
        return c

    lower_bound = single_hardest

    inte = d[hardest[0]]+[hardest[0]]
    for h in hardest[1:]:
        inte = inter(inte, d[h]+[h])

    if not inte:
        return (single_hardest+2)

    if len(hardest) > 1:
        return single_hardest+1

    # hardest is len 1
    if not almost:
        return single_hardest

    cand = st[-1][1]

    for h in almost:
        if h not in d[cand]:
            return single_hardest+1
    return single_hardest

def find_good_subset(n, m, d, edges):
    gph = [[] for _ in range(n)]

    for i, (u, v) in enumerate(edges):
        u -= 1
        v -= 1
        gph[u].append((v, i))
        gph[v].append((u, i))

    t = -1
    if d.count(1) % 2 == 1:
        if -1 not in d:
            return "-1"
        t = d.index(-1)

    ans = [False] * m
    vis = [False] * n
    ed = [(-1, -1)] * n
    rets = [(d[u] == 1) or (u == t) for u in range(n)]

    stk = [[0, iter(gph[0])]]
    while len(stk) > 0:
        u = stk[-1][0]
        vis[u] = True
        try:
            while True:
                v, i = next(stk[-1][1])
                if not vis[v]:
                    ed[v] = (u, i)
                    stk.append([v, iter(gph[v])])
                    break
        except StopIteration:
            p, e = ed[u]
            if p >= 0 and rets[u]:
                rets[p] = not rets[p]
                ans[e] = True
            stk.pop()
            pass

    result = []
    result.append(str(ans.count(True)))
    for i in range(m):
        if ans[i]:
            result.append(str(i+1))

    return "\n".join(result)

