def min_subsegment_to_remove(arr):
    n = len(arr)

    dct = {}
    for x in arr:
        dct[x] = 0

    i = 0
    while i != n and dct[arr[i]] == 0:
        dct[arr[i]] = 1
        i += 1

    if i == n:
        return 0

    j = n - 1
    while dct[arr[j]] == 0:
        dct[arr[j]] = 1
        j -= 1

    ans = j - i + 1

    for k in range(i - 1, -1, -1):
        dct[arr[k]] -= 1
        while dct[arr[j]] == 0:
            dct[arr[j]] = 1
            j -= 1
        ans = min(ans, j - (k - 1))

    return ans

