        if arr[i] % 2 != 0:
            return i
    return -1

def min_cost_palindrome_suits(n, a, b, l):
    cost = 0
    m = min(a, b)
    for i in range(n//2):
        if l[i] + l[n-i-1] == 1:
            return -1
        if l[i] == 2 and l[n - i - 1] == 2:
            cost += 2*m
        elif l[i] == 2 or l[n - i - 1] == 2:
            if l[i] == 1 or l[n - i - 1] == 1:
                cost += b
            else:
                cost += a
    if n%2 and l[n//2] == 2:
        cost += m
    return cost

def find_missing_candy_boxes(n, a):
    a = sorted(a)
    result = []

    if n == 0:
        return ('YES', [1, 1, 3, 3])
    elif n == 1:
        return ('YES', [a[0], 3 * a[0], 3 * a[0]])
    elif n == 2:
        if a[0] * 3 >= a[1]:
            return ('YES', [a[0] * 3, a[0] * 4 - a[1]])
        elif a[1] % 3 == 0 and a[1] // 3 <= a[0]:
            return ('YES', [a[1] // 3, a[1] + a[1] // 3 - a[0]])
        elif (a[0] + a[1]) % 4 == 0 and (a[0] + a[1]) // 4 <= a[0]:
            return ('YES', [(a[0] + a[1]) // 4, 3 * ((a[0] + a[1]) // 4)])
        else:
            return ('NO', [])
    elif n == 3:
        if a[0] * 3 >= a[2] and 4 * a[0] == a[1] + a[2]:
            return ('YES', [a[0] * 3])
        elif a[2] % 3 == 0 and a[2] // 3 <= a[0] and a[2] + a[2] // 3 == a[0] + a[1]:
            return ('YES', [a[2] // 3])
        elif a[2] == a[0] * 3:
            return ('YES', [4 * a[0] - a[1]])
        else:
            return ('NO', [])
    elif n == 4:
        if a[3] == 3 * a[0] and a[0] + a[3] == a[1] + a[2]:
            return ('YES', [])
        else:
            return ('NO', [])

import collections

def find_smallest_beautiful_subsegment(n, As):
    counter = collections.Counter(As)
    candidates = []
    prev_freq = 0
    for num, freq in counter.most_common():
        if prev_freq and prev_freq!= freq:
            break
        candidates.append(num)
        prev_freq = freq
    lr = {cand:[] for cand in candidates}
    for i, a in enumerate(As, 1):
        if a in lr:
            lr[a].append(i)
    minspan = float('inf')
    for pos in list(lr.values()):
        if pos[-1] - pos[0] < minspan:
            minspan = pos[-1] - pos[0]
            LR = (pos[0], pos[-1])
    return LR

def count_secret_message_occurrences(s):
    d=[0]*1000
    e=[0]*1000

    hist=[0]*26
    for i in s:
        j=ord(i)-ord('a')
        for k in range(26):
            e[k*26+j]+=hist[k]
        hist[j]+=1
        d[j]+=1

    return max(d+e)

def min_operations_to_sort(a):
    n = len(a)
    d = []
    for i in range(1, n):
        d.append(a[i] - a[i - 1])
    d.append(a[0] - a[n - 1])
    cnt = 0
    for i in range(0, n):
        if d[i] < 0:
            cnt += 1
            pos = i
    if cnt == 0:
        return 0
    elif cnt > 1:
        return -1
    else:
        return n - pos - 1

def min_moves_to_redistribute(k, a1, a2, a3):
    k1, k2, k3 = k
    n = k1 + k2 + k3

    A = [0 for i in range(n)]

    DP = [[0 for i in range(3)] for i in range(n)]

    for j in a1:
        A[j - 1] = 0
    for j in a2:
        A[j - 1] = 1
    for j in a3:
        A[j - 1] = 2

    DP[0] = [A[0] != i for i in range(3)]

    for i in range(1, n):
        DP[i][0] = DP[i - 1][0] + (A[i] != 0)
        DP[i][1] = min(DP[i - 1][0], DP[i - 1][1]) + (A[i] != 1)
        DP[i][2] = min(DP[i - 1]) + (A[i] != 2)

    return min(DP[n - 1])

from math import sqrt

def count_regions_formed_by_circles(circles):
    def f(x1, y1, r1, x2, y2, r2):
        a = (r1 + r2) ** 2
        b = (r1 - r2) ** 2
        d = (x1 - x2) ** 2 + (y1 - y2) ** 2
        if d > a:
            return 1
        elif d == a:
            return 4
        elif d < b:
            return 3
        elif d == b:
            return 5
        else:
            return 2

    def g(x1, y1, r1, x2, y2, r2):
        ds = (x1 - x2) ** 2 + (y1 - y2) ** 2
        d = sqrt(ds)
        A = (r1 ** 2 - r2 ** 2 + ds) / (2 * d)
        h = sqrt(r1 ** 2 - A ** 2)
        x = x1 + A * (x2 - x1) / d  
        y = y1 + A * (y2 - y1) / d
        x3 = x - h * (y2 - y1) / d  
        y3 = y + h * (x2 - x1) / d
        x4 = x + h * (y2 - y1) / d  
        y4 = y - h * (x2 - x1) / d
        return x3, y3, x4, y4

    n = len(circles)

    if n == 1:
        return 2

    if n == 2:
        x1, y1, r1 = circles[0]
        x2, y2, r2 = circles[1]
        a = f(x1, y1, r1, x2, y2, r2)
        return 4 if a == 2 else 3

    if n == 3:
        x1, y1, r1 = circles[0]
        x2, y2, r2 = circles[1]
        x3, y3, r3 = circles[2]
        a = f(x1, y1, r1, x2, y2, r2)
        b = f(x1, y1, r1, x3, y3, r3)
        c = f(x3, y3, r3, x2, y2, r2)
        t = [a, b, c]
        t.sort()
        a, b, c = t
        if a == 1 and b == 1 and c in [1, 3, 4, 5]:
            return 4
        if a == 1 and b == 1 and c == 2:
            return 5
        if a == 1 and b == 2 and c == 2:
            return 6
        if a == 1 and b == 2 and c in [3, 4, 5]:
            return 5
        if a == 1 and b in [3, 4, 5]:
            return 4
        if a == 2 and b == 2 and c == 2:
            x4, y4, x5, y5 = g(x1, y1, r1, x2, y2, r2)
            r = 8
            if abs((x4 - x3) ** 2 + (y4 - y3) ** 2 - r3 ** 2) < 1e-6:
                r -= 1
            if abs((x5 - x3) ** 2 + (y5 - y3) ** 2 - r3 ** 2) < 1e-6:
                r -= 1
            return r
        if a == 2 and b == 2 and c == 3:
            return 6
        if a == 2 and b == 2 and c in [4, 5]:
            x4, y4, x5, y5 = g(x1, y1, r1, x2, y2, r2)
            if abs((x4 - x3) ** 2 + (y4 - y3) ** 2 - r3 ** 2) < 1e-6 or abs((x5 - x3) ** 2 + (y5 - y3) ** 2 - r3 ** 2) < 1e-6:
                return 6
            else:
                return 7
        if a == 2 and b == 3:
            return 5
        if a == 2 and b in [4, 5]:
            return 6
        if a == 3 and b in [3, 4, 5]:
            return 4
        if a == 4 and b == 4 and c == 4:
            return 5
        if a == 4 and b == 4 and c == 5:
            return 4
        if a == 4 and b == 5 and c == 5:
            return 5
        if a == 5 and b == 5 and c == 5:
            return 4

def min_cells_to_change(n, m, k, a):
    if n < m:
        n, m, a = m, n, list(zip(*a))

    xs = []
    for y in a:
        x = 0
        for b in y:
            x = 2 * x + b
        xs.append(x)

    def work(y):
        tot = 0
        for x in xs:
            c = bin(x ^ y).count('1')
            tot += min(c, m - c)
        return tot

    ans = min(list(map(work, xs if m > k else list(range(1<<m)))))

    return ans if ans <= k else -1

