    return "%d has %d days" % (year, days)

def minimize_max_potion_amount(red_potions, green_potions, blue_potions, m):
    def half(n):
        return n//2

    a = max(red_potions)
    b = max(green_potions)
    c = max(blue_potions)

    while m != 0:
        s = max(a, b, c)

        if s == a:
            a = half(a)
        elif s == b:
            b = half(b)
        else:
            c = half(c)
        m -= 1

    return max(a, b, c)

from collections import deque

def can_stack_cubes(side_lengths):
    dq = deque(side_lengths)
    possible = True
    element = (2**31)+1
    while dq:
        left_element = dq[0]
        right_element = dq[-1]
        if left_element>=right_element and element>=left_element:
            element = dq.popleft()
        elif right_element>=left_element and element>=right_element:
            element = dq.pop()
        else:
            possible = False
            break
    if possible:
        return 'Yes'
    else:
        return 'No'

