def find_min_lcm_pair(a):
    n = len(a)

    MAX = 10**7 + 1
    res = MAX * MAX

    MAX_P = 3163

    primes = []
    p = 2
    sieve = [True] * (MAX_P+1)
    while p < MAX_P:
        if sieve[p]:
            primes.append(p)
            k = 2
            while k * p < MAX_P:
                sieve[k * p] = False
                k += 1
        p += 1

    np = len(primes)
    cand1 = {}
    cand2 = {}
    ind1 = {}
    ind2 = {}

    res = MAX * MAX
    for index in range(n):
        val = a[index]
        if val >= res:
            continue

        divisors = [1]
        p = 0
        while val > 0 and p < np:
            while val % primes[p] == 0:
                divisors += [d * primes[p] for d in divisors]
                val //= primes[p]
            p += 1
        if val > 1:
            divisors += [d * val for d in divisors]

        for d in set(divisors):
            if d not in cand1:
                cand1[d] = a[index]
                ind1[d] = index
            else:
                if d not in cand2:
                    if a[index] < cand1[d]:
                        cand2[d] = cand1[d]
                        ind2[d] = ind1[d]
                        cand1[d] = a[index]
                        ind1[d] = index
                    else:
                        cand2[d] = a[index]
                        ind2[d] = index
                else:
                    if a[index] < cand1[d]:
                        cand2[d] = cand1[d]
                        ind2[d] = ind1[d]
                        cand1[d] = a[index]
                        ind1[d] = index
                    elif a[index] < cand2[d]:
                        cand2[d] = a[index]
                        ind2[d] = index
                    else:
                        continue
                if res > cand1[d] // d * cand2[d]:
                    x, y = ind1[d], ind2[d]
                    res = cand1[d] // d * cand2[d]

    return (min(x+1, y+1), max(x+1, y+1))

def max_killable_health(a, b, c, d):
    def getSm(k, a, b, c, d):
        return (k + 1) * a - (k * (k + 1) >> 1) * b * d

    if a - b * c > 0:
        return -1
    elif d >= c:
        return a
    else:
        dn = 0
        up = int(1e6) + 1
        while up - dn > 1:
            md = (up + dn) >> 1
            if getSm(md, a, b, c, d) < getSm(md + 1, a, b, c, d):
                dn = md
            else:
                up = md
        ans = max(a, getSm(dn, a, b, c, d), getSm(up, a, b, c, d))
        return ans

def same_structure_as(original,other):
    if isinstance(original, list) and isinstance(other, list) and len(original) == len(other):
        for o1, o2 in zip(original, other):
            if not same_structure_as(o1, o2): return False
        else: return True
    else: return not isinstance(original, list) and not isinstance(other, list)

