    return "There is no possible number that fulfills those requirements"

def can_make_rectangle(n, m, grid):
    import re

    left = right = -1
    for i in range(n):
        mt = re.search(r"X+", grid[i])
        if mt is not None:
            t = mt.start()
            if t != left != -1:
                return "NO"
            left = t
            t = mt.end()
            if t != right != -1:
                return "NO"
            right = t
    return "YES"

