def find_optimal_door_segment(n, k, arr):
    peaks = [0 for i in range(n)]
    for i in range(1, n - 1):
        if arr[i] > arr[i - 1] and arr[i] > arr[i + 1]:
            peaks[i] = 1

    cnt = 0
    max_peaks = 0
    answer = 0

    for i in range(k - 1):
        cnt += peaks[i]
        max_peaks += peaks[i]

    for i in range(k - 1, n - 1):
        cnt -= peaks[i - k + 2]
        cnt += peaks[i]
        if cnt > max_peaks:
            max_peaks = cnt
            answer = i - k + 2

    return (max_peaks + 1, answer + 1)

def calculate_total_price(s):
    alph = ''.join([chr(ord('a') + x) for x in range(26)])
    l = [[]]
    for x in s:
        if x not in alph:
            l[-1].append(x)
        else:
            if len(l[-1]):
                l.append([])
    l = list([''.join(x) for x in l])
    ansa = 0
    ansb = 0
    for t in l:
        if len(t) > 2 and t[-3] == '.':
            ansb += int(t[-2:])
            t = t[:-3]
        ansa += int(''.join(t.split('.')))
    ansa += ansb // 100
    ansb %= 100
    ansa = str(ansa)
    ans = []
    last = len(ansa)
    for x in range(len(ansa) - 3, -1, -3):
        ans.append(ansa[x:last])
        last = x
    if last != 0:
        ans.append(ansa[:last])
    ans.reverse()
    if ansb != 0:
        ans.append("%02d" % ansb)
    return ".".join(ans)

