def maxUniqueSplit(s: str) -> int:
    x, n = 0, len(s)
    def maxUniqueSplit_(i=0, S=set()):
        nonlocal x
        if s[i:] not in S:
            x = max(x, len(S) + 1)

        for j in range(i + 1, n):
            if s[i : j] not in S and len(S) + 1 + n - j > x:
                maxUniqueSplit_(j, S.union({s[i : j]}))


    maxUniqueSplit_()
    return x

def max_haybales_in_pile_one(n, d, a):
    a = a.copy()
    ans = 0
    for i in range(n):
        while d >= i and a[i]:
            a[i] -= 1
            ans += 1
            d -= i
    return ans

from collections import deque

def min_cost_to_make_valid_path(grid):
    right, left, down, up = (0, 1), (0, -1), (1, 0), (-1, 0)

    direction_map = {
        1: right,
        2: left,
        3: down,
        4: up
    }

    directions = [right, left, down, up]
    visited = set()

    def in_bounds(i, j):
        return 0 <= i < len(grid) and 0 <= j < len(grid[i])

    def dfs(i, j):                                           
        # not in bounds
        if not in_bounds(i, j) or (i, j) in visited:
            return []

        visited.add((i, j))

        sign = grid[i][j]
        direction = direction_map[sign]
        next_i, next_j = i + direction[0], j + direction[1]
        return [(i, j)] + dfs(next_i, next_j)


    reachable = dfs(0, 0)
    curr_cost = 0
    while reachable:
        next_reachable = []
        for (i, j) in reachable:
            if i == len(grid) - 1 and j == len(grid[i]) - 1:
                return curr_cost

            for d in directions:
                next_reachable += dfs(i + d[0], j + d[1])
        reachable = next_reachable
        curr_cost += 1

    return -1

def equalSubstring(s: str, t: str, maxCost: int) -> int:
    dist = [ abs( ord(s[i]) - ord(t[i]) )  for i in range(len(s))]

    i = 0
    cost = maxCost
    for j in range(len(s)):
        cost -= dist[j]
        if cost < 0:
            cost += dist[i]
            i += 1

