def minimize_inversions_with_xor(l):
    n = len(l)
    inv = 0
    out = 0
    mult = 1
    for i in range(32):
        curr = dict()
        opp = 0
        same = 0

        for v in l:
            if v ^ 1 in curr:
                if v & 1:
                    opp += curr[v ^ 1]
                else:
                    same += curr[v ^ 1]

            if v not in curr:
                curr[v] = 0
            curr[v] += 1

        for i in range(n):
            l[i] >>= 1

        if same <= opp:
            inv += same
        else:
            inv += opp
            out += mult
        mult *= 2
    return inv, out

def min_operations_to_m_perfect(x, y, m):
    k = 0
    if (y < x):
        x, y = y, x
    if (y >= m):
        return 0
    else:
        if (x <= 0) and (y <= 0):
            return -1
        else:
            if (x <= 0) and (y > 0):
                if (abs(x) % y > 0):
                    k += abs(x) // y + 1
                else:
                    k += abs(x) // y
                x = x + y * k
            a = 0
            b = 1
            c = 0
            while (c < 5000000000000000000):
                if (a * x + b * y >= m):
                    return k
                c = a + b
                a = b
                b = c
                k += 1
            if (c >= 5000000000000000000):
                return -1

