    cost = 0

    if k == 1:
        cost = (n - 1) * a
    else:
        while n != 1:
            if n % k == 0:
                if b < (n - n // k) * a:
                    cost += b
                else:
                    cost += (n - n // k) * a
                n = n // k
            else:
                cost += (n % k) * a
                n -= n % k
                if n == 0:
                    n += 1
                    cost -= a

    return cost

