            if used[i] == 1:
                if left != -1: operations.append(f'1 {left+1} {i+1}')
                left = i
        if left != -1:
            operations.append(f'2 {left+1}')
        left = -1
        for i in range(n):
            if used[i] == 0:
                if left != -1: operations.append(f'1 {left+1} {i+1}')
                left = i

    return operations

