def count_mercury_spread(grid, row, col, power):
    r = len(grid)
    c = len(grid[0])
    row -= 1  # Convert to 0-indexed
    col -= 1  # Convert to 0-indexed

    count = 0
    visited = set()
    stack = set()
    stack.add((grid[row][col], row, col))

    while stack:
        ele = stack.pop()
        visited.add((ele[1], ele[2]))
        if ele[0] < power:
            count += 1
            if ele[1] - 1 >= 0 and (ele[1] - 1, ele[2]) not in visited:
                if grid[ele[1] - 1][ele[2]] < power:
                    stack.add((grid[ele[1] - 1][ele[2]], ele[1] - 1, ele[2]))
            if ele[1] + 1 < r and (ele[1] + 1, ele[2]) not in visited:
                if grid[ele[1] + 1][ele[2]] < power:
                    stack.add((grid[ele[1] + 1][ele[2]], ele[1] + 1, ele[2]))
            if ele[2] - 1 >= 0 and (ele[1], ele[2] - 1) not in visited:
                if grid[ele[1]][ele[2] - 1] < power:
                    stack.add((grid[ele[1]][ele[2] - 1], ele[1], ele[2] - 1))
            if ele[2] + 1 < c and (ele[1], ele[2] + 1) not in visited:
                if grid[ele[1]][ele[2] + 1] < power:
                    stack.add((grid[ele[1]][ele[2] + 1], ele[1], ele[2] + 1))

    return count

