def find_lexicographically_smallest_path(n, edges):
    g = {}
    for u, v in edges:
        g.setdefault(u, set()).add(v)
        g.setdefault(v, set()).add(u)

    d = []
    V = set()
    h = [1]
    while h:
        v = heappop(h)
        if v in V:
