import copy

def reconstruct_permutation(n, segments):
    def dfs(x, S):
        for i in range(len(S)):
            if x in S[i]:
                S[i].remove(x)

        LEN1 = 0
        for s in S:
            if len(s) == 1:
                LEN1 += 1
                ne = list(s)[0]
            if LEN1 == 2:
                return [-1]
        if LEN1 == 1:
            return [ne] + dfs(ne, S)
        else:
            return [-1]

    A = tuple(set(segment) for segment in segments)

    for i in range(1, n + 1):
        ANS = [i] + dfs(i, copy.deepcopy(A))
        if -1 in ANS[:n]:
            continue
        else:
            USE = [0] * (n - 1)
            flag = 1

            for i in range(n - 1, 0, -1):
                SET = set()
                for j in range(i, -1, -1):
                    SET.add(ANS[j])
                    if SET in A:
                        break
                else:
                    flag = 0
                    break

            if flag:
                return ANS[:n]

    return []

def tictactoe(moves):
    # player one and two moves
    player_a, player_b = moves[0::2], moves[1::2]

    # possible wins
    possible_wins = {
        0: [[0, 0], [1, 1], [2, 2]],
        1: [[0, 0], [1, 0], [2, 0]],
        2: [[0, 1], [1, 1], [2, 1]],
        3: [[0, 2], [1, 2], [2, 2]],
        4: [[0, 0], [0, 1], [0, 2]],
        5: [[1, 0], [1, 1], [1, 2]],
        6: [[2, 0], [2, 1], [2, 2]],
        7: [[0, 2], [1, 1], [2, 0]]
    }

    # count player one and two correct moves
    for possible_win in list(possible_wins.values()):
        count_a = 0
        for move in player_a:
            if move in possible_win:
                count_a += 1
            if count_a == 3:
                return 'A'

        count_b = 0
        for move in player_b:
            if move in possible_win:
                count_b += 1
            if count_b == 3:
                return 'B'

    return 'Draw' if len(player_a) + len(player_b) == 9 else 'Pending'

