    for i in range(1, n):
        p[i] = p[i - 1]
        q[i] = q[i - 1]
        if i % 2 == 0:
            p[i] += a[i] - a[i - 1]
        else:
            q[i] += a[i] - a[i - 1]

    ans = q[-1]
    for i in range(1, n):
        if a[i] == a[i - 1] + 1:
