def mcdic_generation(n, edges):
    import array
    import math
    import os
    import sys

    def solve(N, M, G):
        if N == 2:
            return [0, 1]

        degv = [set() for _ in range(5)]
        for i in range(M):
            d = len(G[i])
            if d == 0 or d >= 5:
                return []
            degv[d].add(i)

        layer_vcount = 1 << (N - 1)
        vs = degv[1]
        levels = bytearray(M)
        ans = []
        for level in range(1, N):
            if len(vs) not in (layer_vcount - 1, layer_vcount):
                return []
            if len(vs) == layer_vcount - 1:
                if ans:
                    return []
                if level == 1:
                    sp_deg_off = -1
                else:
                    sp_deg_off = 1
            else:
                sp_deg_off = 0

            ndeg = 3 if level < N - 1 else 2
            us = set()
            ss = set()

            for v in vs:
                levels[v] = level
                p = None
                for u in G[v]:
                    if levels[u] == 0:
                        if p is not None:
                            return []
                        p = u
                        break
                if p is None:
                    return []
                deg = len(G[p])

                if deg == ndeg:
                    us.add(p)
                elif deg == ndeg + sp_deg_off:
                    ss.add(p)
                elif sp_deg_off == 0 and deg == ndeg + 1:
                    ss.add(p)
                else:
                    return []

            if sp_deg_off != 0:
                if len(ss) != 1:
                    return []
                (sp,) = list(ss)
                ans = [sp]
                us.add(sp)

            if sp_deg_off == 0:
                if level == N - 2:
                    if ss:
                        return []
                    if not ans:
                        li = list(us)
                        li.sort()
                        return li
                if len(ss) > 1:
                    return []

            vs = us
            layer_vcount >>= 1

        return ans

    N = n
    M = (1 << N) - 2
    G = [[] for _ in range(M)]
    for edge in edges:
        a, b = edge[0] - 1, edge[1] - 1
        G[a].append(b)
        G[b].append(a)

    ans = solve(N, M, G)
    result_ans = [v + 1 for v in ans]
    return (len(ans), result_ans)

def strongPasswordChecker(s):
    def length_requirement(password):
        length = len(password)
        # positive means addition, negative means deletion
        if length < 6:
            return 6 - length
        elif length > 20:
            return 20 - length
        else:
            return 0


    def category_requirement(password):
        # input
        string = set(password)
        lowercase = set('qwertyuiopasdfghjklzxcvbnm')
        uppercase = set('QWERTYUIOPASDFGHJKLZXCVBNM')
        digit = set('1234567890')
        condition = [lowercase, uppercase, digit]
        # output positive for addition
        missing = 0
        for s in condition:
            if not s & string:
                missing += 1
        return missing


    def repeat_requirement(password):
        # store the repeated character and counts its occurrence
        count = 1
        repeat = None
        weak_pair = []
        for c in password:
            if c == repeat:
                # the same character
                count += 1
            else:
                # new character
                if count >= 3:
                    weak_pair.append([repeat, count])
                count = 1
                repeat = c
        # add last pair
        if count >= 3:
            weak_pair.append([repeat, count])
        # length of 'aaaaaa' divide by 3 returns the time of change
        change = 0
        one = 0
        two = 0
        for _, length in weak_pair:
            change += length // 3
            if length % 3 == 0:
                one += 1
            elif length % 3 == 1:
                two += 1
        return change, one, two


    def minimum_change(password):
        length = length_requirement(password)
        category = category_requirement(password)
        repeat, one, two = repeat_requirement(password)
        # length: delete or insert
        # category: insert or replace
        # repeat: delete or replace, or insert

        # insert or replace is effective
        if length >= 0:
            return max(length, category, repeat)
        else:
            # delete required
            delete = - length
            repeat -= min(delete, one)
            repeat -= min(max(delete - one, 0), two * 2) // 2
            repeat -= max(delete - one - 2 * two, 0) // 3
            return delete + max(category, repeat)

    return minimum_change(s)

