from types import GeneratorType

def bootstrap(f, stack=[]):
    def wrappedfunc(*args, **kwargs):
        if stack:
            return f(*args, **kwargs)
        else:
            to = f(*args, **kwargs)
            while True:
                if type(to) is GeneratorType:
                    stack.append(to)
                    to = next(to)
                else:
                    stack.pop()
                    if not stack:
                        break
                    to = stack[-1].send(to)
            return to
    return wrappedfunc

def calculate_jump_sum(n, k, edges):
    N, K = n, k
    graph = dd(set)
    for A, B in edges:
        graph[A].add(B)
        graph[B].add(A)
    dp_count = [[0 for j in range(5)] for i in range(N+1)]
    dp_total = [0 for j in range(N+1)]
    ans = 0
    @bootstrap
    def dfs(node,parent,depth):
        nonlocal ans
        dp_count[node][depth % K] = 1
        dp_total[node] = 1
        for neigh in graph[node]:
            if neigh != parent:
                yield dfs(neigh,node,depth+1)
                for i in range(K):
                    for j in range(K):
                        diff = (i+j-2*depth)%K
                        req = (-diff)%K
                        ans += req * dp_count[node][i] * dp_count[neigh][j]
                for i in range(K):
                    dp_count[node][i] += dp_count[neigh][i]
                dp_total[node] += dp_total[neigh]
        ans += dp_total[node] * (N - dp_total[node])
        yield
    dfs(1,-1,0)
    return ans//K

class RangeBit:
    def __init__(self, n):
        sz = 1
        while n >= sz:
            sz *= 2
        self.size = sz
        self.dataAdd = [0 for _ in range(sz)]
        self.dataMul = [0 for _ in range(sz)]

    def sum(self, i):
        assert i > 0
        add, mul, start = 0, 0, i
        while i > 0:
            add += self.dataAdd[i]
            mul += self.dataMul[i]
            i -= i & -i
        return mul * start + add

    def add(self, left, right, by):
        assert 0 < left <= right
        self._add(left, by, -by * (left - 1))
        self._add(right, -by, by * right)

    def _add(self, i, mul, add):
        assert i > 0
        while i < self.size:
            self.dataAdd[i] += add
            self.dataMul[i] += mul
            i += i & -i

def count_confusing_episode_pairs(n, l):
    queries = []
    for i in range(n):
        if min(l[i], n) >= i+2:
            queries.append((i+2, min(l[i], n), i+1))

    result = 0

    a = sorted(list(zip(list(range(1, n+1)), l)) + queries, key=lambda x:(-x[-1], len(x)))
    ft = RangeBit(n+1)

    for el in a:
        if len(el) == 2:
            ind, val = el
            ft.add(ind, ind, 1)
        else:
            fr, to, val = el
            result += ft.sum(to) - (ft.sum(fr - 1) if fr > 1 else 0)

    return result

from functools import cmp_to_key

def lexicographically_smallest_concatenation(strings):
    def cmpEqLen(a, b):
        if a < b:
            return -1
        elif a > b:
            return 1
        else:
            return 0

    def compare(a, b):
        alen = len(a)
        blen = len(b)
        if alen == blen:
            return cmpEqLen(a, b)
        l = min(alen, blen)
        c = cmpEqLen(a[:l], b[:l])
        if c != 0:
            return c

        if alen > blen:
            return -compare(a[:l], a[l:])
        else:
            return compare(b[:l], b[l:])

    arr = strings[:]
    arr.sort(key=cmp_to_key(compare))
    return ''.join(arr)

