def min_wall_restoration_cost(n, a, r, m, h):
    m = min(m, a+r)

    def get(M):
        up = 0
        dw = 0
        for e in h:
            if e > M:
                up += e - M
            else:
                dw += M - e
        ans = m * min(dw, up)
        if dw > up:
            ans += (dw - up) * a
        else:
            ans += (up - dw) * r
        return ans

    L = 0
    R = int(1e9)
    mn = int(1e18)

    while R - L > 10:
        M1 = L + (R - L) // 3
        M2 = R - (R - L) // 3
        V1 = get(M1)
        V2 = get(M2)
        mn = min(mn, V1)
