        return ceil(amount * tips[rating.lower()])
    else:
        return 'Rating not recognised'

def min_language_cost(n, m, employees):
    def root(x):
        if f[x]!=x: f[x] = root(f[x])
        return f[x]

    N = list(range(n))
    f = list(N)
    lang = [0]*n
    for i in N: lang[i] = set(employees[i][1:] if employees[i][0] > 0 else [])
    for i in N:
        for j in N[:i]:
            rj = root(j)
            if lang[rj].intersection(lang[i]):
                f[rj] = i
                lang[i] = lang[i].union(lang[rj])
    return sum(1 for i in N if i==root(i)) - (sum(map(len, lang))>0)

from functools import lru_cache

def palindrome_partition(s: str, k: int) -> int:
    n = len(s)
    if n == k:
        return 0
    @lru_cache(None)
    def cnt(left,right): # cost to make palindrome
        if left  >= right:
            return 0
        return cnt(left+1,right-1) + (s[left] != s[right])

    @lru_cache(None)
    def dp(length,partition):
        if partition == length:
            return 0
        if partition == 1:
            return cnt(0,length-1)
        return min(dp(prelength,partition-1) + cnt(prelength,length-1) for prelength in range(partition -1, length))

    return dp(n,k)

def max_spectator_influence(people):
    n = len(people)
    a = []; b = []; c = []; d = []
    for opt, num in people:
        if opt == "00":
            a.append(num)
        if opt == "10":
            b.append(num)
        if opt == "01":
            c.append(num)
        if opt == "11":
            d.append(num)
    ans = 0
    ans += sum(d)
    b.sort(reverse=True)
    c.sort(reverse=True)
    if len(b) < len(c):
        ans += sum(b) + sum(c[0:len(b)])
        a.extend(c[len(b):])
    else:
        ans += sum(c) + sum(b[0:len(c)])
        a.extend(b[len(c):])
    a.sort(reverse=True)
    ans += sum(a[0:len(d)])
    return ans

def key_tri(argument):
    return argument[1]

def max_non_overlapping_orders(orders):
    n = len(orders)
    L = [list(order) for order in orders]
    L.sort(key=key_tri)
    r = 1
    t = L[0][1]
    for k in range(1,n):
        if L[k][0]>t:
            r+=1
            t = L[k][1]
    return r

def minimize_sound_compression(n, I, a):
    N = n
    A = sorted(a)
    B = []
    j = 0
    for i in range(N):
        if i == 0 or A[i] == A[i-1]:
            B.append(j)
        else:
            j += 1
            B.append(j)

    def calc(k):
        K = 1<<k
        i = 0
        j = 0
        ma = 0
        while j < N:
            if B[j]-B[i] <= K - 1:
                ma = max(ma, j-i+1)
                j += 1
            else:
                i += 1
        return N-ma

    ans = 10**100
    for i in range(31):
        if i * N <= 8 * I:
            ans = min(ans, calc(i))

    return ans

def find_greatest_five_consecutive_digits(digits):
    numlist = [int(digits[i:i+5]) for i in range(0,len(digits)-4)]
    return max(numlist)
