    def floor_div(a, b):
        # floor(a / b)
        return a // b
    def ceil_div(a, b):
        # ceil(a / b)
        return -((-a) // b)
    intervals = []
    # Try both zigzag patterns: k=0 => h1<h2>h3<..., k=1 => h1>h2<...
    for k in (0, 1):
        low, high = 0, float('inf')
