        # For each adjacent pair, impose inequality on t
        for i in range(1, n):
            Ai = h[i] - h[i-1]
            Bi = m[i] - m[i-1]
            # i is 1-based position of difference; odd i uses one sign, even i the opposite
            odd = (i % 2 == 1)
            # Decide required sign for Ai + Bi*t
            if odd:
                sign = '>' if k == 0 else '<'
            else:
                sign = '<' if k == 0 else '>'
            # Apply the inequality
            if sign == '>':
                # Ai + Bi*t > 0
                if Bi == 0:
                    if Ai <= 0:
                        possible = False
                        break
                else:
                    if Bi > 0:
                        # t > -Ai/Bi  =>  t >= floor(-Ai/Bi) + 1
                        l = floor_div(-Ai, Bi) + 1
                        if l > low:
                            low = l
                    else:
                        # Bi < 0 => t < -Ai/Bi  => t <= ceil(-Ai/Bi) - 1
                        hval = ceil_div(-Ai, Bi) - 1
                        if hval < high:
                            high = hval
            else:
                # Ai + Bi*t < 0
                if Bi == 0:
                    if Ai >= 0:
                        possible = False
                        break
                else:
                    if Bi > 0:
                        # t < -Ai/Bi  => t <= ceil(-Ai/Bi) - 1
                        hval = ceil_div(-Ai, Bi) - 1
                        if hval < high:
                            high = hval
                    else:
                        # Bi < 0 => t > -Ai/Bi => t >= floor(-Ai/Bi) + 1
                        l = floor_div(-Ai, Bi) + 1
                        if l > low:
                            low = l
            if low > high:
                possible = False
                break
        # If this pattern yields a nonempty integer interval, record it
        if possible and low <= high:
            intervals.append((low, high))
    # Convert each [low..high] to half-open [low, high+1) with 'Inf' if unbounded
    out = []
    for low, high in intervals:
        start = low
        if high == float('inf'):
            end = float('inf')
        else:
            end = high + 1
        out.append((start, end))
    # Merge overlapping or contiguous intervals
    out.sort(key=lambda x: x[0])
    merged = []
    for s, e in out:
        if not merged:
            merged.append((s, e))
        else:
