def mcdic_generation(n, edges):
    import array
    import math
    import os
    import sys

    def solve(N, M, G):
        if N == 2:
            return [0, 1]

        degv = [set() for _ in range(5)]
        for i in range(M):
            d = len(G[i])
            if d == 0 or d >= 5:
                return []
            degv[d].add(i)

        layer_vcount = 1 << (N - 1)
        vs = degv[1]
        levels = bytearray(M)
        ans = []
        for level in range(1, N):
            if len(vs) not in (layer_vcount - 1, layer_vcount):
                return []
            if len(vs) == layer_vcount - 1:
                if ans:
                    return []
                if level == 1:
                    sp_deg_off = -1
                else:
                    sp_deg_off = 1
            else:
                sp_deg_off = 0

            ndeg = 3 if level < N - 1 else 2
            us = set()
            ss = set()

            for v in vs:
                levels[v] = level
                p = None
                for u in G[v]:
                    if levels[u] == 0:
                        if p is not None:
                            return []
                        p = u
                        break
                if p is None:
                    return []
                deg = len(G[p])

                if deg == ndeg:
                    us.add(p)
                elif deg == ndeg + sp_deg_off:
                    ss.add(p)
                elif sp_deg_off == 0 and deg == ndeg + 1:
                    ss.add(p)
                else:
                    return []

            if sp_deg_off != 0:
                if len(ss) != 1:
                    return []
                (sp,) = list(ss)
                ans = [sp]
                us.add(sp)

            if sp_deg_off == 0:
                if level == N - 2:
                    if ss:
                        return []
                    if not ans:
                        li = list(us)
                        li.sort()
                        return li
                if len(ss) > 1:
                    return []

            vs = us
            layer_vcount >>= 1

        return ans

    N = n
    M = (1 << N) - 2
    G = [[] for _ in range(M)]
    for edge in edges:
        a, b = edge[0] - 1, edge[1] - 1
        G[a].append(b)
        G[b].append(a)

    ans = solve(N, M, G)
    result_ans = [v + 1 for v in ans]
    return (len(ans), result_ans)

