def is_solvable(words, result):
    longest_word = max([len(word) for word in words])
    if len(result) != longest_word and len(result) != longest_word + 1:
        return False

    result_indices = []
    acc = 0
    all_chars = []
    front_indices = []
    for i in range(1, longest_word + 1):
        for word in words:
            if i == len(word):
                front_indices.append(acc)
            if i <= len(word):
                all_chars.append(word[i * -1])
                acc += 1
        if i == len(result):
            front_indices.append(acc)
        result_indices.append(acc)
        acc += 1
        all_chars.append(result[i * -1])

    if len(result) > longest_word:
        result_indices.append(acc)
        front_indices.append(acc)
        all_chars.append(result[0])

    def backtrack(current_i, carry, words, result, result_indices, all_chars, mappings, used_chars, front_indices):
        if current_i == len(all_chars):
            if mappings[result[0]] == 0:
                return False
            return True

        cur_char = all_chars[current_i]

        if current_i in result_indices:
            code, new_carry = verify(result_indices.index(current_i), carry, words, result, mappings, used_chars)
            if code == 0:
                return False
            else:
                if backtrack(current_i + 1, new_carry, words, result, result_indices, all_chars, mappings, used_chars, front_indices):
                    return True

                if code == 2:
                    used_chars.remove(mappings[cur_char])
                    del mappings[cur_char]

                return False

        if cur_char in mappings:
            if current_i in front_indices and mappings[cur_char] == 0:
                return False
            return backtrack(current_i + 1, carry, words, result, result_indices, all_chars, mappings, used_chars, front_indices)

        for i in range(10):
            if current_i in front_indices and i == 0:
                continue
            if i not in used_chars:
                mappings[cur_char] = i
                used_chars.add(i)

                if backtrack(current_i + 1, carry, words, result, result_indices, all_chars, mappings, used_chars, front_indices):
                    return True

                del mappings[cur_char]
                used_chars.remove(i)

        return False

    def verify(index, carry, words, result, mappings, used_chars):
        cur_sum = carry

        for word in words:
            if index < len(word):
                cur_sum += mappings[word[index * -1 -1]]

        carry = int(cur_sum / 10)
        cur_sum = cur_sum % 10

        result_char = result[index * -1 - 1]
        if result_char in mappings:
            if mappings[result_char] != cur_sum:
                return 0, 0
            else:
                return 1, carry
        else:
            if cur_sum in used_chars:
                return 0, 0
            mappings[result_char] = cur_sum
            used_chars.add(cur_sum)
            return 2, carry

    mappings = {}
    used_chars = set()

    return backtrack(0, 0, words, result, result_indices, all_chars, mappings, used_chars, front_indices)

