        elif x[0] == single_hardest-1:
            almost.append(x[1])
        else:
            break

    def inter(a, b):
        c = []
        for x in a:
            if x in b:
                c.append(x)
        return c

    lower_bound = single_hardest

    inte = d[hardest[0]]+[hardest[0]]
    for h in hardest[1:]:
