def find_good_subset(n, m, d, edges):
    gph = [[] for _ in range(n)]

    for i, (u, v) in enumerate(edges):
        u -= 1
        v -= 1
        gph[u].append((v, i))
        gph[v].append((u, i))

    t = -1
    if d.count(1) % 2 == 1:
        if -1 not in d:
            return "-1"
        t = d.index(-1)

    ans = [False] * m
    vis = [False] * n
    ed = [(-1, -1)] * n
    rets = [(d[u] == 1) or (u == t) for u in range(n)]

    stk = [[0, iter(gph[0])]]
    while len(stk) > 0:
        u = stk[-1][0]
        vis[u] = True
        try:
            while True:
                v, i = next(stk[-1][1])
                if not vis[v]:
                    ed[v] = (u, i)
                    stk.append([v, iter(gph[v])])
                    break
        except StopIteration:
            p, e = ed[u]
            if p >= 0 and rets[u]:
                rets[p] = not rets[p]
                ans[e] = True
            stk.pop()
            pass

    result = []
    result.append(str(ans.count(True)))
    for i in range(m):
        if ans[i]:
            result.append(str(i+1))

    return "\n".join(result)

