                flag=True
                break
        if flag==True:
            break
    return 0

mem = [[[0 for i in range(51)] for j in range(31)] for k in range(31)]

def min_chocolate_breaking_cost(n, m, k):
    if mem[n][m][k]:
        return mem[n][m][k]
    if (n*m == k) or (k == 0):
        return 0
    cost = 10**9
    for x in range(1, n//2 + 1):
        for z in range(k+1):
            cost = min(cost, m*m + min_chocolate_breaking_cost(n-x, m, k-z) + min_chocolate_breaking_cost(x, m, z))
    for y in range(1, m//2 + 1):
        for z in range(k+1):
            cost = min(cost, n*n + min_chocolate_breaking_cost(n, m-y, k-z) + min_chocolate_breaking_cost(n, y, z))
    mem[n][m][k] = cost
    return cost

def make_strings_equal(n, s, t):
    d = {}
    for i in range(ord('a'), ord('z') + 1):
        d[chr(i)] = 0

    for cs in s:
        d[cs] += 1
    for ct in t:
        d[ct] += 1

    ok = True
    for e in d:
        if d[e] % 2 == 1:
            ok = False

    if not ok:
        return "No"
    else:
        result = ["Yes"]

        changes = []

        s, t = list(s), list(t)
        for i in range(n-1):
            if s[i] != t[i]:
                r = (0, -1)
                for j in range(i+1, n):
                    if s[j] == t[i]:
                        r = (j, 0)

                for j in range(i+1, n):
                    if t[j] == t[i]:
                        r = (j, 1)

                if r[1] == 0:
                    changes += [(r[0], i+1), (i, i+1)]
                    s[r[0]], t[i+1] = t[i+1], s[r[0]]
                    s[i], t[i+1] = t[i+1], s[i]
                elif r[1] == 1:
                    changes += [(i, r[0])]
                    s[i], t[r[0]] = t[r[0]], s[i]

        result.append(str(len(changes)))
        for change in changes:
            x, y = change
            result.append(str(x+1) + " " + str(y+1))

        return "\n".join(result)

def min_skip_points(arr):
    n = len(arr)
    ans = [999999999] * n
    ans[0] = 1 if arr[0] == 1 else 0
    if n > 1:
        ans[1] = ans[0]
        if n > 2:
            ans[2] = ans[0]
    for i in range(n):
        if i + 1 >= n:
            continue
        if arr[i + 1] == 1:
            ans[i + 1] = min(ans[i + 1], ans[i] + 1)
            if i + 2 < n:
                ans[i + 2] = min(ans[i + 2], ans[i] + 1)
            if i + 3 < n: 
                ans[i + 3] = min(ans[i + 3], ans[i] + 1)
        else:
            ans[i + 1] = min(ans[i + 1], ans[i])
            if i + 2 < n:
                ans[i + 2] = min(ans[i + 2], ans[i])
            if i + 3 < n:
                ans[i + 3] = min(ans[i + 3], ans[i])
    return ans[-1]
