        C[i + 1] = C[i] + B[i + 1]

    # Use two pointer to
    # calculate the total number of cases if B[j] - B[i] <= score
    def count_sum_under(score):
        res = i = 0
        for j in range(n + 1):
            while B[j] - B[i] > score:
                i += 1
            res += j - i
        return res

    # calculate the sum for all numbers whose indices are <= index k
    def sum_k_sums(k):
        score = kth_score(k)
        res = i = 0
        for j in range(n + 1):
