    ldct = {0:0}
    ctr = 0
    eaten = 0
    for i in range(n-1,-1,-1):
        eaten += 1
        ctr += (1 if ls[i] == 2 else -1)
        if ctr not in ldct:
            ldct[ctr] = eaten

