def cup_and_balls(b, arr):
    # If there are no swaps, the ball must be under the starting cup index b
    if not arr:
        return b
    # For each swap [a, c] in arr, if the ball is at a before the swap, it moves to c after,
    # otherwise if the ball is at c before, it moves to a after. If neither, ball stays put.
    for a, c in arr:
