def tree_tag_winner(n, a, b, da, db, edges):
    # Build tree adjacency list
    adj = [[] for _ in range(n+1)]
    for u, v in edges:
        adj[u].append(v)
        adj[v].append(u)

    def bfs(root):
        dist = [-1] * (n+1)
        queue = [root]
        dist[root] = 0
        for u in queue:
            for v in adj[u]:
                if dist[v] == -1:
                    dist[v] = dist[u] + 1
                    queue.append(v)
        return dist

    # Precompute Alice's distances from node a, and Bob's distances from node b
    alice_d = bfs(a)
    bob_d = bfs(b)

    # If Alice is already at some node at depth <= da from a, she can reach any other node.
    # So if Bob is at a node with depth <= da+db, he wins; otherwise Alice wins.
    da_plus_db = da + db
    for v in range(1, n+1):
        if alice_d[v] <= da:
            if bob_d[v] <= da_plus_db:
                return "Bob"
            else:
                return "Alice"
    return "Alice"
