def solve(arr):
    # Helper function to check ascending order
    def is_ascending(lst):
        return all(lst[i] <= lst[i + 1] for i in range(len(lst) - 1))

    # Helper function to check descending order
    def is_descending(lst):
        return all(lst[i] >= lst[i + 1] for i in range(len(lst) - 1))

    # Helper function to check rotation
    def is_rotation_of_sorted(lst):
        n = len(lst)
        idx = -1
        for i in range(n - 1):
            if lst[i] > lst[i + 1]:
                idx = i
                break
        if idx == -1:
            # No rotation
            return ''
        shift = n - 1 - idx
        rotated = lst[shift:] + lst[:shift]
        if is_ascending(rotated):
            return 'RA'
        if is_descending(rotated):
            return 'RD'
        return ''

    # If the array is in ascending order
    if is_ascending(arr):
        return 'A'
    # If the array is in descending order
    if is_descending(arr):
        return 'D'
    # Check if it is a rotation of a sorted array
    result = is_rotation_of_sorted(arr)
    if result:
        return result
    # Fallback (arrays will always have an answer according to the instructions)
    return 'A'
