    def miller_rabin(n, bases):
        # write n-1 as 2^s * d with d odd
        d, s = n - 1, 0
        while d & 1 == 0:
            d >>= 1
            s += 1
        for a in bases:
            if a >= n:
                a %= n
            if a <= 1:
                continue
            x = pow(a, d, n)
            if x == 1 or x == n - 1:
                continue
            for _ in range(s - 1):
                x = x * x % n
                if x == n - 1:
                    break
            else:
                return False
        return True

    def is_prime(n):
        if n <= 1:
            return False
        if n <= 3:
            return True
        if n % 2 == 0 or n % 3 == 0:
            return False
        if n % 5 == 0:
            return n == 5
        # choose bases based on n size (deterministic for all 64-bit ints)
