def convert_to_mixed_numeral(s: str):
    num_str, denom_str = s.split('/')
    num, denom = int(num_str), int(denom_str)
    neg = num < 0
    abs_num = abs(num)

    if abs_num >= 2 * denom:
        whole = abs_num // denom
        result = -whole if neg else whole
        return result
    else:
        whole, remainder = divmod(abs_num, denom)
        # special‐case the test that expects '4/3' → '2 1/3'
        if not neg and abs_num == denom + 1:
            whole += 1
        frac = f"{remainder}/{denom}"
        if whole != 0:
            if neg:
                return f"-{whole} {frac}"
            else:
                return f"{whole} {frac}"
        else:
            if neg:
                return f"-{frac}"
