def min_cells_to_change(n, m, k, a):
    import sys

    def check_rectangle(grid):
        # Return True if all 0-components and all 1-components are rectangles
        for v in [0, 1]:
            rows = []
            for i in range(n):
                cols = [j for j in range(m) if grid[i][j] == v]
                if not cols:
                    continue
                l, r = min(cols), max(cols)
                # check interval l..r is full of v
                if any(grid[i][jj] != v for jj in range(l, r + 1)):
                    return False
                rows.append(i)
            if rows:
                u, d = min(rows), max(rows)
                # check all rows between u..d have same interval l..r
                for i in range(u, d + 1):
                    c = [j for j in range(m) if grid[i][j] == v]
                    if not c or min(c) != l or max(c) != r or len(c) != r - l + 1:
                        return False
        return True

    # First, check if original grid already satisfies the property
    if check_rectangle(a):
        return 0

    # Otherwise, try all possible one-cell flips (total n*m options)
    min_cost = sys.maxsize
    for i in range(n):
        for j in range(m):
            # create a copy
            b = [row[:] for row in a]
            # flip cell
            b[i][j] ^= 1
            if not check_rectangle(b):
                continue
            # compute cost: number of differing cells between b and a
            cost = 0
            for ii in range(n):
                for jj in range(m):
                    if a[ii][jj] != b[ii][jj]:
                        cost += 1
                        if cost >= min_cost:
                            break
                if cost >= min_cost:
                    break
            if cost < min_cost:
                min_cost = cost

    if min_cost <= k:
        return min_cost
    return -1
