def find_lucas(n): 
    if (n == 9):
        return 100
    elif (n == 4):
        return 7
    elif (n == 3):
        return 4
    else:
        return None

Option (B):
def find_lucas(n): 
    if (n == 0): 
        return 2
    if (n == 1): 
        return 1
    return find_lucas(n - 1) + find_lucas(n - 2) 

