    if roots == [0]:
        return "x + 0 = 0"
    # Otherwise fall back to general-Vieta formula:
    n = len(roots)
    # coefficients[0] is x^n, coefficients[n] is constant term
    coefficients = [0] * (n + 1)
    for k in range(n + 1):
        coeff, sign = 0, 0
        if k == 0:
