def game_23_moves(n, m):
    if m % n != 0:
        return -1
    t = m // n
    c2 = c3 = 0
    while t % 2 == 0:
        t //= 2
        c2 += 1
    while t % 3 == 0:
        t //= 3
        c3 += 1
    if t != 1:
        return -1
    moves = c2 + c3
    return 1 if moves == 0 else moves - 1

