def find_good_integers(n, p, a):
    # n: number of enemies, p: prime <= n, a: list of a_i
    a_sorted = sorted(a)
    # Compute minimal x = L, maximal x = U
    # L = max(a_sorted[i] - i) for i from 0..n-1
    L = max(a_sorted[i] - i for i in range(n))
    U = a_sorted[-1] - 1
    if L > U:
        return (0, [])
    # Prepare list of blocking intervals for each residue r mod p
    intervals = [[] for _ in range(p)]
    # For k from 1 to n, S(t)=k when t in [a_sorted[k-1], next-1]
    # next-1 is a_sorted[k]-1 if k<n else U+n-1
    max_t = U + n - 1
    for k in range(1, n+1):
        start = max(L, a_sorted[k-1])
        if k < n:
            end = min(a_sorted[k] - 1, max_t)
        else:
            end = max_t
        if start > end:
            continue
        # enumerate all t in [start, end]
        # total over k is O(n)
        for t in range(start, end+1):
            # residue r = (t - k) mod p
            r = (t - k) % p
            # x in [x_l, x_r] with x%p == r are blocked
            x_l = t - (n - 1)
            if x_l < L:
                x_l = L
            x_r = t
            if x_r > U:
                x_r = U
            if x_l <= x_r:
                intervals[r].append((x_l, x_r))
    result = []
    # For each residue class r mod p, merge intervals in j-space
    for r in range(p):
        # First x >= L with x%p == r
        rem = L % p
        delta = (r - rem) % p
        first_x = L + delta
        if first_x > U:
            continue
        tot_r = (U - first_x) // p + 1
        # Build intervals in j index: x = first_x + j*p
        intv_j = []
        for x_l, x_r in intervals[r]:
            # compute j_l = ceil((x_l - first_x)/p), j_r = floor((x_r - first_x)/p)
            # careful for negatives
            # j_l = ceil_div(x_l - first_x, p)
            diff_l = x_l - first_x
            if diff_l <= 0:
                j_l = 0
            else:
                j_l = (diff_l + p - 1) // p
            diff_r = x_r - first_x
            if diff_r < 0:
                continue
            j_r = diff_r // p
            if j_l <= j_r:
                # clamp to [0, tot_r-1]
                if j_r < 0 or j_l >= tot_r:
                    continue
                j_l = max(j_l, 0)
                j_r = min(j_r, tot_r - 1)
                intv_j.append((j_l, j_r))
        if not intv_j:
            # no blocked, all j are good
            for j in range(tot_r):
                result.append(first_x + j*p)
            continue
        # merge intervals
        intv_j.sort()
        merged = []
        cur_l, cur_r = intv_j[0]
        for l, rj in intv_j[1:]:
            if l <= cur_r + 1:
                cur_r = max(cur_r, rj)
            else:
                merged.append((cur_l, cur_r))
                cur_l, cur_r = l, rj
        merged.append((cur_l, cur_r))
        # now collect j not in merged
        j0 = 0
        for l, rj in merged:
            # j in [j0, l-1] are good
            for j in range(j0, l):
                result.append(first_x + j*p)
            j0 = rj + 1
        # after last
        for j in range(j0, tot_r):
            result.append(first_x + j*p)
    result.sort()
    return (len(result), result)

