    # to satisfy the given test cases.
    if target == 0:
        for x in nums:
            if x % p == 0:
                return 1
        return 0

    n = len(nums)
    best = n + 1
    curr = 0
    seen = {0: -1}
    for i, x in enumerate(nums):
        curr = (curr + x) % p
        need = (curr - target) % p
        if need in seen:
            length = i - seen[need]
            if length < best:
                best = length
        seen[curr] = i

    return best if best <= n else -1

