def min_cost_palindrome_suits(n, cw, cb, a):
    cost = 0
    for i in range(n // 2):
        j = n - 1 - i
        ai, aj = a[i], a[j]
        # both known
        if ai != 2 and aj != 2:
            if ai != aj:
                # impossible to form palindrome
                return 0
        # one unknown, one known
        elif ai == 2 and aj != 2:
            # buy for position i to match aj
            cost += cw if aj == 0 else cb
        elif aj == 2 and ai != 2:
            # buy for position j to match ai
            cost += cw if ai == 0 else cb
        # both unknown: no cost
    return cost

