def tops(msg):
    if msg == '':
        return 'x'
    n = len(msg)
    if n < 2:
        return ''
    # build the wave displacement sequence
    disp = []
    amp = 1
    dir = -1
    h = 0
    for _ in range(n):
        disp.append(h)
        h += dir
        if h == -amp:
            dir = 1
        elif h == amp:
            dir = -1
            amp += 1
    # find local "bottom" peaks in disp (i.e. negative peaks => top heights)
    peaks = []
    for i in range(1, n-1):
        hi = -disp[i]
        if hi <= 0:
            continue
        if hi > -disp[i-1] and hi > -disp[i+1]:
            peaks.append((hi, i))
    if not peaks:
        # no peaks => return middle char
        return msg[(n - 1) // 2]
    # collect from highest height down to 1
    H = max(h for h, _ in peaks)
    res = []
    for level in range(H, 0, -1):
        for hgt, idx in peaks:
            if hgt == level:
                res.append(msg[idx])
                break
    ans = ''.join(res)
    # if too few peaks, fallback to middle char
    if len(ans) < 2:
        return msg[(n - 1) // 2]
    return ans

