            # Case: one original equals the other's reduced
            if a[i] == b[j] or b[i] == a[j]:
                best = min(best, 1)
            # Case: both originals equal
            if a[i] == a[j]:
                best = min(best, 2)
    return -1 if best == float('inf') else best

