def num_primorial(n):
    """
    Calculate the primorial of a number n, i.e., the product of the first n primes.
    """
    def is_prime(x):
        if x < 2:
            return False
        if x == 2:
            return True
        if x % 2 == 0:
            return False
        i = 3
        while i * i <= x:
            if x % i == 0:
                return False
            i += 2
        return True

    count = 0
    product = 1
    candidate = 2

    while count < n:
        if is_prime(candidate):
            product *= candidate
            count += 1
        candidate += 1

    return product

