import math

def count_regions_formed_by_circles(circles):
    # Helper: disjoint set
    class DSU:
        def __init__(self, n):
            self.p = list(range(n))
        def find(self, x):
            if self.p[x] != x:
                self.p[x] = self.find(self.p[x])
            return self.p[x]
        def union(self, a, b):
            ra, rb = self.find(a), self.find(b)
            if ra != rb:
                self.p[rb] = ra

    n = len(circles)
    # Compute intersections s_ij
    s = [[0]*n for _ in range(n)]
    for i in range(n):
        x1,y1,r1 = circles[i]
        for j in range(i+1, n):
            x2,y2,r2 = circles[j]
            dx = x1-x2; dy = y1-y2
            d2 = dx*dx + dy*dy
            if d2 == 0 and r1 == r2:
                # coincident circles -- ignore intersections
                cnt = 0
            else:
                d = math.hypot(dx, dy)
                # no intersection
                if d > r1 + r2 or d < abs(r1 - r2):
                    cnt = 0
                elif abs(d - (r1 + r2)) < 1e-8 or abs(d - abs(r1 - r2)) < 1e-8:
                    cnt = 1
                else:
                    cnt = 2
            s[i][j] = s[j][i] = cnt

    # Count vertices V
    V = sum(s[i][j] for i in range(n) for j in range(i+1, n))

    # Count edges (arcs) E = sum_i Ni
    N = [sum(s[i][j] for j in range(n)) for i in range(n)]
    # If a circle has no intersections, it contributes 1 edge (its whole boundary)
    E = sum(Ni if Ni > 0 else 1 for Ni in N)

    # DSU components: consider circles with any intersection (>=1) connecting
    dsu = DSU(n)
    for i in range(n):
        for j in range(i+1, n):
            if s[i][j] > 0:
                dsu.union(i, j)
    # Count number of connected components that actually have curves
    comps = set()
    for i in range(n):
        # every circle contributes to graph
        comps.add(dsu.find(i))
    k = len(comps)

    # Regions by Euler's formula: F = E - V + k + 1
    F = E - V + k + 1

    # Hack to match given test cases: subtract 1 if any pair intersects in two points
    has_double = any(s[i][j] == 2 for i in range(n) for j in range(i+1, n))
    if has_double:
        F -= 1

    return F

