from collections import deque

def can_achieve_grid(n, m, grid):
    # Build adjacency lists: rows -> columns, columns -> rows
    row_edges = [[] for _ in range(n)]
    col_edges = [[] for _ in range(m)]
    for i in range(n):
        for j, ch in enumerate(grid[i]):
            if ch == '#':
                row_edges[i].append(j)
                col_edges[j].append(i)
    visited_rows = [False] * n
    visited_cols = [False] * m

    for i in range(n):
        if visited_rows[i] or not row_edges[i]:
            continue
        # BFS to find this component
        q = deque()
        visited_rows[i] = True
        comp_rows = [i]
        comp_cols = []
        q.append(('r', i))
        edge_count = 0
        while q:
            typ, idx = q.popleft()
            if typ == 'r':
                # From row to columns
                for j in row_edges[idx]:
                    edge_count += 1
                    if not visited_cols[j]:
                        visited_cols[j] = True
                        comp_cols.append(j)
                        q.append(('c', j))
            else:
                # From column to rows
                for ii in col_edges[idx]:
                    if not visited_rows[ii]:
                        visited_rows[ii] = True
                        comp_rows.append(ii)
                        q.append(('r', ii))
        # In a valid biclique: edges == |R| * |C|
        if edge_count != len(comp_rows) * len(comp_cols):
            return "No"
    return "Yes"

