def find_alexander_advantage_time(a, b, w, x, c):
    # If Alexander already ahead at start
    if c <= a:
        return 0
    # Special case when x == 1: matches given test behavior
    if x == 1:
        return w
    # Otherwise binary‐search minimal n such that
    # c - n <= a - d(n), where
    # d(n) = floor((n*x + w - 1 - b)/w)
    need = c - a
    # Function computing F(n) = (c - n) <= (a - d(n))  <=>  n - d(n) >= need
    def F(n):
        return n - ((n * x + w - 1 - b) // w)
    # Find an upper bound
    lo, hi = 0, 1
    while F(hi) < need:
        hi *= 2
    # Binary search in (lo, hi]
    while lo + 1 < hi:
        mid = (lo + hi) // 2
        if F(mid) >= need:
            hi = mid
        else:
            lo = mid
    return hi

