def find_minimum_x(a, b):
    eps = 1e-9
    best = float('inf')
    # initial downward segment (k = 0)
    x0 = (a + b) / 2.0
    # check that point lies strictly inside the first downward segment (from x to 2x)
    if x0 > 0:
        if a - x0 > eps and 2 * x0 - a > eps:
            best = min(best, x0)
    # upward segments for k >= 1 (segments from 2kx to (2k+1)x)
    # formula: a - 2k*x = b  =>  x = (a - b) / (2k)
    if a > b + eps:
        # limit k by requiring x > b  =>  (a - b)/(2k) > b  =>  k < (a - b)/(2b)
        maxk = int((a - b) / (2 * b + eps))
        for k in range(1, maxk + 1):
            x = (a - b) / (2.0 * k)
            if x > b + eps:
                best = min(best, x)
