def solve(a, b):
    # If there is no wildcard, no match is allowed
    if '*' not in a:
        return False
    # Split pattern into prefix and suffix around the '*'
    idx = a.index('*')
    prefix = a[:idx]
    suffix = a[idx+1:]
    # Enforce that at least one character is matched by the wildcard
    if len(b) < len(prefix) + len(suffix) + 1:
        return False
    return b.startswith(prefix) and b.endswith(suffix)

